sys_script_client.script:
function onLoad() {
    if (g_form.isNewRecord()) {
        setAccomplishmentInvisible();
        setAssignedTo();
    }
}

function setAssignedTo() {
    g_form.setValue("assigned_to", g_user.userID);
}

function setAccomplishmentInvisible() {
    var accomplishment_fields = ["accomplished", "accomplished_time", "notes", "overdue"];
    accomplishment_fields.forEach(function(field) {
        g_form.setVisible(field, false);
    });

}
